import React, { useState, useEffect } from 'react';
import { FollowerChart } from './FollowerChart';
import { Button, Pane, Text, Heading, majorScale } from 'evergreen-ui';

function ButtonControls(timeUnit, onTimeUnitChange) {
    return (
        <Pane display="flex" alignItems="center" marginX={majorScale(2)}>
            <Button>Day</Button>
            <Button>Month</Button>
            <Button>Year</Button>
        </Pane>
    )
}

export {ButtonControls}
