import React, { useState } from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, TimeScale } from 'chart.js';
import 'chartjs-adapter-date-fns';
import { Data } from './Data';

ChartJS.register(
  TimeScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function FollowerChart() {
  const [timeUnit, setTimeUnit] = useState('day');

  const options = {
    scales: {
      x: {
        type: 'time',
        time: {
          parser: 'yyyy-MM-dd',
          unit: 'day',
          tooltipFormat: 'yyyy-MM-dd',
          displayFormats: {
            day: 'MMM dd yyyy'
        }
        },
        distribution: "linear",
        title: {
          display: true,
          text: "Date"
        }
      },
      y: {
        title: {
          display: true,
          text: "Followers",
        },
        min: 0
      },
    },
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: false,
        text: 'Follower Count',
      },
    },
  };

  const data = {
    labels: Data.map((data) => data.date),
    datasets: [
      {
        label: 'Instagram Followers',
        data: Data.map((data) => data.followers),
        borderColor: 'rgb(50, 168, 111)',
        backgroundColor: 'rgba(50, 168, 111, 0.5)',
      }
    ],
  };

  return <Line options={options} data={data} />;
}

export { FollowerChart }
